#pragma config(Motor,  port1,           frontMotor,    tmotorNone, openLoop)
#pragma config(Motor,  port2,           backRightMotor, tmotorNone, openLoop)
#pragma config(Motor,  port3,           backLeftMotor, tmotorNone, openLoop)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

#define HALF_SPEED;
#define FULL_SPEED;
#define STATIONARY;
const int stationary = 0;
const int fullSpeed = 127;
const int halfSpeed = 63;

/*
*runs front Motor at half speed for .5 sec, stop for .5 sec,
*fullspeed for .5 sec, then stop
*/
task Test()
{
	motor[frontMotor] = halfSpeed;
	wait1Msec(500);
	motor[frontMotor] = stationary;
	wait1Msec(500);
	motor[frontMotor] = fullSpeed;
	wait1Msec(500);
	motor[frontMotor] = stationary;
}

task main()
{
		startTask(Test);
}
