#pragma config(Motor,  port1,           leftMotor,     tmotorVex393_HBridge, openLoop, driveLeft)
#pragma config(Motor,  port10,          rightMotor,    tmotorVex393_HBridge, openLoop, driveRight)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//
/*
*everything on the left is negated because of mirrored motors
*/
#define HALF_SPEED 63;
#define FULL_SPEED 127;
#define STATIONARY 0;
#define BACK_HALF -63;
#define BACK_FULL -127;

/*
*runs a Motor at half backwards speed for .5 sec, stop for .5 sec,
*fullspeed for .5 sec, then stop
*/
void startTest(tMotor m)
{
	motor[m] = BACK_HALF;
	wait1Msec(500);
	motor[m] = STATIONARY;
	wait1Msec(500);
	motor[m] = FULL_SPEED;
	wait1Msec(500);
	motor[m] = STATIONARY;
}

/*
*turn left without moving forward, pivot turn
*/
void left()
{
	motor[rightMotor] = 63;
	motor[leftMotor] = 63;
	wait1Msec(500);
}

void right()
{
	motor[leftMotor] = -63;
	motor[rightMotor] = -63;
	wait1Msec(500);
}

void move()
{ 	while(true) {
		if(vexRT[Btn7u] ==1){
			motor[rightMotor] = HALF_SPEED;
			motor[leftMotor] = -HALF_SPEED;
		}
		else if(vexRT[Btn7d] ==1){
			motor[rightMotor] = -HALF_SPEED;
			motor[leftMotor] = HALF_SPEED;
		}
		else if(vexRT[Btn7r] ==1){
			motor[rightMotor] = -HALF_SPEED;
			motor[leftMotor] = -HALF_SPEED;
		}
		else if(vexRT[Btn7l] ==1){
			motor[rightMotor] = HALF_SPEED;
			motor[leftMotor] = HALF_SPEED;
		}
		else{
			motor[rightMotor] = STATIONARY;
			motor[leftMotor] = STATIONARY;
		}
	}
}
task main()
{
	//	startTest(rightMotor);
	//	startTest(leftMotor);
	left();
	right();
	move();
}
